import java.awt.*;import java.awt.event.*;import java.util.*;import java.text.*;import ij.*;import ij.gui.*;import ij.process.*;/** * <hr> * <p><b>Plugin of ImageJ:</b><br> * FFT<br> * * <p><b>Author:</b><br> * Daniel Sage <a href="mailto:daniel.sage@epfl.ch?subject=SNR Plugin">daniel.sage@epfl.ch</a><br> * Swiss Federal Institute of Technology Lausanne, * Biomedical Imaging Group, * CH-1015 Lausanne, Switzerland, * <a href="http://bigwww.epfl.ch">http://bigwww.epfl.ch/</a><br> * * <p><b>Version:</b><br> * 3 September 2006<br> * * <p><b>Copyright</b><br> * Copyright © 2006, Swiss Federal Institute of Technology, Lausanne, Switzerland, (EPFL)<br> * * <hr> * */ 	public class AddGrayFrame_ {	public AddGrayFrame_() {			ImagePlus imp = WindowManager.getCurrentImage();		if (imp == null) {			IJ.error("No open image.");			return;		}				GenericDialog gd = new GenericDialog("Grayed Frame");		gd.addNumericField("Width of the frame",  10, 0);		gd.showDialog();		if (gd.wasCanceled())			return;		int width = (int)gd.getNextNumber();					ImageAccess input  = new ImageAccess(imp.getProcessor());				ImageAccess out = CodeClass.addGrayFrame(input, width);		out.show("Gray Frame");			}}